    private void totalPrice_tfPropertyChange(java.beans.PropertyChangeEvent evt) {//GEN-FIRST:event_totalPrice_tfPropertyChange
        totalPrice_tf.setEditable(false);
        totalPrice_tf.setText(Double.toString(Product.totalPrice));
    }//GEN-LAST:event_totalPrice_tfPropertyChange

    private void discountedPrice_tfPropertyChange(java.beans.PropertyChangeEvent evt) {//GEN-FIRST:event_discountedPrice_tfPropertyChange
        discountedPrice_tf.setEditable(false);

        discountedPrice_tf.setText(String.format("%.2f", Product.discount()));
    }//GEN-LAST:event_discountedPrice_tfPropertyChange

    private void bagType_comboBoxActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bagType_comboBoxActionPerformed

        if(bagType_comboBox.getSelectedItem().equals("Cloth Bag(₺2)")){
            
            if(flag){
                Product.totalPrice -= 1;
            }
            Product.totalPrice += 2;
            flag=true;
            type="cloth";
            
            totalPrice_tf.setText(Double.toString(Product.totalPrice));
            discountedPrice_tf.setText(String.format("%.2f", Product.discount()));
            
        }
        
        if(bagType_comboBox.getSelectedItem().equals("Plastic Bag(₺1)")){
            
            if(flag){
                Product.totalPrice -= 2;
            }
            Product.totalPrice += 1;
            flag=true;
            type="plastic";
            

            totalPrice_tf.setText(Double.toString(Product.totalPrice));
            discountedPrice_tf.setText(String.format("%.2f", Product.discount()));
  
        }

        if(bagType_comboBox.getSelectedItem().equals("Select")){
            confirm_btn.setEnabled(false);
            if(type.equals("cloth")){
                Product.totalPrice -= 2;
            }
            if(type.equals("plastic")){
                Product.totalPrice -= 1;
            }
            
            flag=false;

            totalPrice_tf.setText(Double.toString(Product.totalPrice));
            discountedPrice_tf.setText(String.format("%.2f", Product.discount()));
        }
        if(!bagType_comboBox.getSelectedItem().equals("Select")){
            confirm_btn.setEnabled(true);
        }

    }//GEN-LAST:event_bagType_comboBoxActionPerformed

    private void fast_checkboxActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_fast_checkboxActionPerformed
        
        if(fast_checkbox.isSelected()){
            Product.totalPrice += 10;
        }
        
        totalPrice_tf.setText(Double.toString(Product.totalPrice));
        discountedPrice_tf.setText(String.format("%.2f", Product.discount()));
        
        if(!fast_checkbox.isSelected()){
            Product.totalPrice -= 10;
        }
       
        totalPrice_tf.setText(Double.toString(Product.totalPrice));
        discountedPrice_tf.setText(String.format("%.2f", Product.discount()));
        
    }//GEN-LAST:event_fast_checkboxActionPerformed

