    private void gifts_taPropertyChange(java.beans.PropertyChangeEvent evt) {//GEN-FIRST:event_gifts_taPropertyChange
        gifts_ta.setText("GIFTS YOU WON DUE TO EXCEEDING CERTAIN NUMBER\nOF PRODUCTS IN PURCHASING FROM CATEGORIES:"
                + "\n************************************************\n");
        Clean clean = new Clean();
        PersonalCare perCare = new PersonalCare();
        Other other = new Other();
        Stationary stationary = new Stationary();
        Food food = new Food() {
            @Override
            public String consumptionTime() {
                throw new UnsupportedOperationException("Not supported yet."); 
            }

            @Override
            public String storageCondition() {
                throw new UnsupportedOperationException("Not supported yet.");
            }
        };
        gifts_ta.setEditable(false);
        
        if(ShoppingScreen.cleanCount >=3){
            gifts_ta.append(clean.gift());
        }
        if(ShoppingScreen.careCount >=4){
            gifts_ta.append(perCare.gift());
        }
        if(ShoppingScreen.otherCount >=5){
            gifts_ta.append(other.gift());
        }
        if(ShoppingScreen.stationaryCount >=3){
            gifts_ta.append(stationary.gift());
        }
        if(ShoppingScreen.foodCount >= 7){
            gifts_ta.append(food.gift());
        }
        
    }//GEN-LAST:event_gifts_taPropertyChange

